nasal.load:
#print("LOAD bo105 ", cmdarg().getPath());
var self = cmdarg();

#--begin compat-- (load random variant for fg/plib)
var dir = getprop("/sim/fg-root") ~ "/Aircraft/bo105/Models/Variants";
var files = [];
foreach (var file; directory(dir))
	if (substr(file, -4) == ".xml")
		append(files, file);

var filename = dir ~ "/" ~ files[rand() * size(files)];
var targetnode = self.getPath();
fgcommand("loadxml", props.Node.new({ filename: filename, targetnode: targetnode, }));
var mat = self.getNode("sim/model/bo105/material", 1);
mat.getNode("emblem/texture", 1).setValue("../../../Models/Fauna/cow.rgb");
#--end compat--

var emblem_in = self.getNode("sim/multiplay/generic/string[0]", 1);
var emblem_out = self.getNode("sim/model/bo105/material/emblem/texture", 1);

var livery_update = aircraft.livery_update.new("Aircraft/bo105/Models/Variants", 10.3, func {
	var e = emblem_in.getValue();
	if (e != nil)
		emblem_out.setValue("Textures/Emblems/" ~ e ~ ".png");
});

var aliases = [];
for (var i = 0; i < 6; i += 1) {
	var door = self.getNode("sim/model/bo105/doors/door[" ~ i ~ "]/position-norm", 1);
	var generic = self.getNode("sim/multiplay/generic/float[" ~ i ~ "]", 1);
	door.alias(generic);
	append(aliases, door);
}

nasal.unload:
#print("UNLOAD bo105 ", cmdarg().getPath());
livery_update.stop();
foreach (var a; aliases)
	a.unalias();
